mapper.select:
SELECT id, user_id, type, title, content, is_read, created_at
FROM notifications
WHERE id = #{id}

mapper.update:
UPDATE notifications
SET type = #{type},
    title = #{title},
    content = #{content},
    is_read = #{isRead}
WHERE id = #{id}

UPDATE notifications
SET is_read = 1
WHERE id = #{id}

UPDATE notifications
SET is_read = 1
WHERE user_id = #{userId}
