mapper.select:
SELECT id, team_name, description, invite_code, creator_id, created_at, updated_at
FROM teams
WHERE id = #{id}

SELECT id, team_name, description, invite_code, creator_id, created_at, updated_at
FROM teams
WHERE invite_code = #{inviteCode}

mapper.update:
UPDATE teams
SET team_name = #{teamName},
    description = #{description}
WHERE id = #{id}
