mapper.select:
SELECT id, team_id, user_id, role, joined_at
FROM team_members
WHERE team_id = #{teamId} AND user_id = #{userId}

SELECT id, team_id, user_id, role, joined_at
FROM team_members
WHERE team_id = #{teamId}

SELECT id, team_id, user_id, role, joined_at
FROM team_members
WHERE user_id = #{userId}

mapper.update:
UPDATE team_members
SET role = #{role}
WHERE team_id = #{teamId} AND user_id = #{userId}
